sp_widget.script:
(function() {
  var producerSysId = '31688d54c3457210c7e81d12b401315c'; // your record producer sys_id

  var gr = new GlideRecord('sc_cat_item_producer');
  if (gr.get(producerSysId)) {
    data.recordProducer = $sp.getRecordProducer(producerSysId);
    data.header = gr.getValue('name') || "Place an Order";
    data.subheader = "Fill out the details below to request pet shop items";
  } else {
    data.error = "⚠️ Record Producer not found or inaccessible.";
  }
})();
