sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // ğŸŸ¢ Run only on insert
    if (current.operation() != 'insert') return;

    gs.info("ğŸ¾ Async BR triggered for order: " + current.number);

    var orderId = current.getUniqueValue();

    // ğŸ”¹ Query cart items that belong to this order and are not yet linked
    var cart = new GlideRecord("x_1607167_pet_shop_pet_shop_cart");
    cart.addQuery("parent_order", orderId);
    cart.addQuery("ordered", true);
    cart.addQuery("order_linked", false);
    cart.query();

    var created = 0;
    gs.info("ğŸ” Found " + cart.getRowCount() + " cart items linked to order " + current.number);

    while (cart.next()) {
        // âš¡ Create order detail record
        var detail = new GlideRecord("x_1607167_pet_shop_pet_shop_order_details");
        detail.initialize();
        detail.pet_shop_order = orderId;
        // Use getDisplayValue() if field is a reference
        detail.item_name = cart.item ? cart.item.getDisplayValue() : '';
        detail.brand = cart.getValue("brand");
        detail.category = cart.getValue("category");
        detail.quantity = cart.getValue("quantity");
        detail.price = cart.getValue("price");
        detail.image = cart.getValue("image");
        detail.insert();

        // ğŸ”¹ Mark cart item as linked
        cart.order_linked = true;
        cart.update();

        created++;
    }

    gs.info("âœ… Created " + created + " order details for order: " + current.number);

})(current, previous);
