sys_script_include.script:
var ExportCurrentMonthOrders = Class.create();
ExportCurrentMonthOrders.prototype = {
    initialize: function() {},

    getCSV: function() {
        var csv = '"Order Number","Order For","Created","Total Amount","Item Name"\n';
        var gr = new GlideRecord('order'); // Replace 'order' with your table name

        // Filter for current month
        var startOfMonth = new GlideDateTime();
        startOfMonth.setDisplayValue(gs.beginningOfThisMonth());

        var endOfMonth = new GlideDateTime();
        endOfMonth.setDisplayValue(gs.endOfThisMonth());

        gr.addQuery('created', '>=', startOfMonth);
        gr.addQuery('created', '<=', endOfMonth);
        gr.query();

        while(gr.next()) {
            csv += '"' + gr.getValue('number') + '","' +
                   gr.getValue('order_for') + '","' +
                   gr.getValue('created') + '","' +
                   gr.getValue('total_amount') + '","' +
                   gr.getValue('item_name') + '"\n';
        }

        return csv;
    },

    type: 'ExportCurrentMonthOrders'
};
