sys_security_acl.script:
// Field-level write ACL for 'assigned_to' field
(function() {
  // managers always
  if (gs.hasRole('x_1607167_pet_shop.Pet Shop Manager')) {
    return true;
  }

  // if the user is the assigned_to already allow editing (useful for assigned clerk)
  var mySysId = gs.getUserID();
  if (current.assigned_to && current.assigned_to == mySysId) {
    return true;
  }

  // clerks may set assigned_to only when the order is currently unassigned
  if (gs.hasRole('x_1607167_pet_shop.Pet Shop Clerk')) {
    if (!current.assigned_to || String(current.assigned_to) === '' || (current.assigned_to.nil && current.assigned_to.nil())) {
      return true; // unassigned -> clerk can set it (to self or another clerk)
    }
  }

  return false;
})();
