sys_security_acl.script:
// Table-level write ACL script for x_1607167_pet_shop_pet_shop_order
(function() {
  // managers always can
  if (gs.hasRole('x_1607167_pet_shop.Pet Shop Manager')) {
    return true;
  }

  // clerks: allowed only if order is unassigned OR assigned to current user
  if (gs.hasRole('x_1607167_pet_shop.Pet Shop Clerk')) {
    // for new records (create -> write runs on update too), allow clerk to create/write if assigned_to is null/empty
    try {
      var assignedTo = current.assigned_to;
      // if assigned_to field is empty/nil, any clerk can write (so they can add assigned_to)
      if (!assignedTo || String(assignedTo) === '' || assignedTo.nil && assignedTo.nil()) {
        return true;
      }
      // if assigned_to equals current user -> allow
      var mySysId = gs.getUserID();
      if (assignedTo == mySysId) {
        return true;
      }
      // otherwise not allowed
      return false;
    } catch (e) {
      // conservative default: deny on unexpected error
      gs.error('Order write ACL error: ' + e);
      return false;
    }
  }

  // others: no write
  return false;
})();
